	play_custom_action_do("CS_dex", "lc03_scene01_dex_46")
	teleport_to_slate_pos("CS_snt1", -4.74647, 0.0495167, -0.792308, "slate_church_dexroom")
	play_custom_action_do("CS_snt1", "lc03_scene01_snt1_46")
	delay(0.3)
	fade_out(0.5)
end

function cutscene_abort()
	letterbox_fade_out(true)
	fade_out(0.5)
end

function cutscene_end()
	stop_custom_actions("CS_ang")
	stop_custom_actions("CS_vic")
	stop_custom_actions("CS_dex")
	stop_custom_actions("CS_snt1")
	stop_custom_actions("CS_snt2")
	stop_custom_actions("CS_snt3")
	stop_custom_actions("CS_snt4")
	stop_custom_actions("CS_snt5")
	stop_custom_actions("#PLAYER#")
	stop_custom_actions("CS_pist01")
	stop_custom_actions("CS_pist02")
	stop_custom_actions("CS_car")
	letterbox_fade_out(true)
	fade_out(0.5)
end
