function cutscene_abort()
	shader_lod_restore()
	spawning_peds(true)
	letterbox_fade_out(true)
	fade_out(0.5)
	set_level_lights(true)
	spawning_cars(true)
	time_of_day_character_shadows_enable(true)
	drawbridge_force_closed()
end

function cutscene_end()
	shader_lod_restore()
	spawning_peds(true)
	drawbridge_force_closed()
	stop_custom_actions("CS_angcar")
	stop_custom_actions("CS_gang")
	stop_custom_actions("CS_angelo")
	stop_custom_actions("#PLAYER#")
	stop_custom_actions("CS_dex")
	stop_custom_actions("CS_pccar")
	stop_custom_actions("CS_CargoShip")
	time_of_day_character_shadows_enable(true)
	letterbox_fade_out(true)
	fade_out(0.5)
	set_level_lights(true)
	spawning_cars(true)
end
